import { RegisterService } from './../../services/register.service';
import { Person } from 'src/app/models/person.model';
import { NgForm } from '@angular/forms';
import { Component } from '@angular/core';
import { ContactData } from 'src/app/models/contact-data.model';
import { Address } from 'src/app/models/address.model';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {

  constructor(private registerService : RegisterService) {

  }
  onSubmit(loginForm: NgForm) {
    const formValue = loginForm.value;

    const address = new Address(
      formValue.country,
      formValue.county,
      formValue.street,
      formValue.number,
      formValue.postalIndex
    );

    const contactData = new ContactData(
      formValue.email,
      formValue.phone,
      address
    );

    const newPerson = new Person(
      formValue.personalCode,
      formValue.firstName,
      formValue.lastName,
      formValue.password,
      contactData
    );

    this.registerService.register(newPerson).subscribe();
  }

}
<form (ngSubmit)="onSubmit(addPersonForm)" #addPersonForm="ngForm" class="border border-light p-5">
    <h3>Registreeru</h3>
    <div class="form-group col-md-3 mb-3">
        <label for="firstName">Eesnimi</label>
        <input ngModel name="firstName" type="text" id="firstName" class="form-control" placeholder="Eesnimi">
    </div>
    <div class="form-group col-md-3 mb-3">
        <label for="lastName">Perenimi</label>
        <input ngModel name="lastName" type="text"  class="form-control" id="lastName" placeholder="Perenimi">
    </div>

    <div class="form-group col-md-3 mb-3">
        <label for="personalCode">Isikukood</label>
        <input ngModel name="personalCode" type="text" class="form-control" id="personalCode" placeholder="Isikukood">
    </div>

    <div class="form-group mb-3 col-md-3">
        <label for="password">Parool</label>
        <input ngModel name="password" type="password" class="form-control" id="password" placeholder="Parool">
    </div>

    <div class="form-group col-md-3 mb-3">
        <label for="email">Email</label>
        <input ngModel name="email" type="email" class="form-control" id="email" placeholder="Email">
    </div>

    <div class="form-group col-md-3 mb-3">
        <label for="phone">Telefoninumber</label>
        <input ngModel name="phone" type="text" class="form-control" id="phone" placeholder="Telefoninumber">
    </div>

    <div class="form-group col-md-3 mb-3">
        <label for="country">Riik</label>
        <input ngModel name="country" type="text" class="form-control" id="country" placeholder="Riik">
    </div>

    <div class="form-group col-md-3 mb-3">
        <label for="county">Maakond</label>
        <input ngModel name="county" type="text" class="form-control" id="county" placeholder="Maakond">
    </div>

    <div class="form-group col-md-3 mb-3">
        <label for="street">Tänav</label>
        <input ngModel name="street" type="text" class="form-control" id="street" placeholder="Tänav">
    </div>

    <div class="form-group col-md-3 mb-3">
        <label for="number">Number</label>
        <input ngModel name="number" type="text" class="form-control" id="number" placeholder="Number">
    </div>

    <div class="form-group col-md-3 mb-3">
        <label for="postalIndex">Postiindeks</label>
        <input ngModel name="postalIndex" type="text" class="form-control" id="postalIndex" placeholder="Postiindeks">
    </div>

    <button class="btn btn-success" type="submit">Registreeru</button>
</form>
